mapper.select:
SELECT concat(t3.param,':',t2.op) param FROM t_sys_role t1 JOIN t_sys_role_operate t2 JOIN t_sys_menu t3
ON t1.id = t2.role AND t2.menu = t3.id

	AND t1.id = (SELECT role FROM t_sys_manager WHERE managerid = #{userId});

select a.* from t_sys_operate a left join t_sys_role_operate b on
(a.menu = b.menu and a.op = b.op) left join t_sys_role d on (b.role =
d.id) left join t_sys_manager f
on (d.id = f.role) where a.menu =
#{menu} and f.managerid =#{managerid}

	and a.op = #{op}
